mapper.select:
SELECT ft.typeImg,fi.id,fi.infoTitle,fi.infoContent,fi.infoCurrentTime,fi.infoSendStaffCode,fi.typeName, fir.status,fir.id firId,fir.infoId,fir.integralStatus, dp.姓名,dp.部门,dp.事业部,fit.infoTypeName,fit.infoTypeContent from flow_info fi INNER JOIN flow_info_accept fia ON fi.id=fia.infoId INNER JOIN flow_type ft ON ft.typeName=fi.typeName INNER JOIN datacenter_personnel dp on fi.infoSendStaffCode=dp.人员编码 LEFT JOIN flow_info_type fit FORCE INDEX(ity) on fit.infoId=fi.id LEFT JOIN flow_info_read fir on fi.id=fir.infoId and staffCode=#{user.staffCode} where infoAcceptName in (#{user.staffCode},#{user.department} ,#{user.BU},'公司')
AND fi.typeName=#{typeName}
AND fi.infoTitle like #{pattern}
order by status, infoCurrentTime desc

SELECT fi.id from flow_info fi
INNER JOIN flow_info_accept fia ON fi.id=fia.infoId 

LEFT JOIN flow_info_read fir on fir.infoId=fi.id and fir.staffCode=#{user.staffCode}

where infoAcceptName in (#{user.staffCode},#{user.department} 
,#{user.BU},'公司')
and fir.status  IS null
